package other;

import java.util.Date;
import javafx.scene.control.Button;
import javafx.scene.control.CheckBox;

public class Ride {

  public String getDriver() {
    return driver;
  }

  public void setDriver(String driver) {
    this.driver = driver;
  }

  public String getDest() {
    return dest;
  }

  public void setDest(String dest) {
    this.dest = dest;
  }

  public Date getDate() {
    return date;
  }

  public void setDate(Date date) {
    this.date = date;
  }

  public String getStartP() {
    return startP;
  }

  public void setStartP(String startP) {
    this.startP = startP;
  }

  public Integer getSeats() {
    return seats;
  }

  public void setSeats(Integer seats) {
    this.seats = seats;
  }

  public Button getMessege() {
    return messege;
  }

  public void setMessege(Button messege) {
    this.messege = messege;
  }

  public CheckBox getCheckBox() {
    return checkBox;
  }

  public void setCheckBox(CheckBox checkBox) {
    this.checkBox = checkBox;
  }

  private String driver;
  private String dest;
  private Date date;
  private String startP;
  private Integer seats;
  private Button messege;
  private CheckBox checkBox;

  public Ride(String driver, String dest, String startP, Date date, int seats) {
    this.driver = driver;
    this.dest = dest;
    this.date = date;
    this.startP = startP;
    this.seats = seats;
    this.messege = new Button();
    this.checkBox = new CheckBox();
  }
}